package com.sarfaraj.newsapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;

import android.os.Bundle;
import android.view.View;
import android.widget.Toast;

import com.android.volley.AuthFailureError;
import com.android.volley.Request;
import com.android.volley.RequestQueue;
import com.android.volley.Response;
import com.android.volley.VolleyError;
import com.android.volley.toolbox.JsonObjectRequest;
import com.android.volley.toolbox.StringRequest;
import com.android.volley.toolbox.Volley;
import com.sarfaraj.newsapp.Adapters.CategoryAdapter;
import com.sarfaraj.newsapp.Adapters.NewsAdapter;
import com.sarfaraj.newsapp.Models.CategoryItemModel;
import com.sarfaraj.newsapp.Models.NewsItemModel;
import com.sarfaraj.newsapp.databinding.ActivityMainBinding;

import org.json.JSONArray;
import org.json.JSONException;
import org.json.JSONObject;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.Map;

public class MainActivity extends AppCompatActivity implements CategoryAdapter.OnCategoryClick {

    //api key  pub_39057464ce1fba2ee9df2b39a34a0cdc9450
    ActivityMainBinding binding;
    ArrayList<CategoryItemModel> categoryArraylist;
    ArrayList<NewsItemModel> newsArraylist;

    CategoryAdapter categoryAdapter;
    NewsAdapter newsAdapter;
    private String newsUrl="https://newsdata.io/api/1/news?apikey=pub_39057464ce1fba2ee9df2b39a34a0cdc9450&country=in&language=en";
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        binding = ActivityMainBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());

        categoryArraylist=new ArrayList<>();
        newsArraylist=new ArrayList<>();

        getCategory();
        categoryAdapter=new CategoryAdapter(categoryArraylist,this,this::categoryClicked);
        binding.idCategoryRecView.setAdapter(categoryAdapter);

        getNewsFromApi(newsUrl);
        newsAdapter=new NewsAdapter(newsArraylist,this);
        binding.idNewsRecView.setAdapter(newsAdapter);
    }

    private void getCategory(){
        categoryArraylist.clear();

        categoryArraylist.add(new CategoryItemModel("Business","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Politics","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Technlogy","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Sports","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Environment","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Entertainment","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Food","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Health","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Science","data:image/jpeg;base64,"));
        categoryArraylist.add(new CategoryItemModel("Top",""));
    }

    public void getNewsFromApi(String uri){
        binding.idProgressBar.setVisibility(View.VISIBLE);
        newsArraylist.clear();
        JsonObjectRequest jsonObjectRequest=new JsonObjectRequest(Request.Method.GET, uri, null, new Response.Listener<JSONObject>() {
            @Override
            public void onResponse(JSONObject response) {

                try {
                    JSONArray jsonArray=response.getJSONArray("results");
                    for (int i = 0; i < jsonArray.length(); i++) {
                        JSONObject jsonObject=jsonArray.getJSONObject(i);
                        String imageUrl=jsonObject.getString("image_url");
                        String desc=jsonObject.getString("description");
                        String title=jsonObject.getString("title");
                        String articleLink=jsonObject.getString("link");

                        newsArraylist.add(new NewsItemModel(imageUrl, title, desc,articleLink));

                    }
                    newsAdapter.notifyDataSetChanged();
                    binding.idProgressBar.setVisibility(View.GONE);
                } catch (JSONException e) {
                    Toast.makeText(MainActivity.this, "error1", Toast.LENGTH_LONG).show();
                }
            }
        }, new Response.ErrorListener() {
            @Override
            public void onErrorResponse(VolleyError error) {
                binding.idProgressBar.setVisibility(View.GONE);
                Toast.makeText(MainActivity.this, "error", Toast.LENGTH_LONG).show();
            }
        });
        RequestQueue queue=Volley.newRequestQueue(getApplicationContext());
        queue.add(jsonObjectRequest);
    }


    @Override
    public void categoryClicked(int position) {
        CategoryItemModel categoryItemModel=categoryArraylist.get(position);
        String category=categoryItemModel.getCategory_text();
        getNewsFromApi("https://newsdata.io/api/1/news?apikey=pub_39057464ce1fba2ee9df2b39a34a0cdc9450&country=in&language=en&category="+category);
    }
}